.hero-background {
  background: url("../images/PaintSplatterBG.jpeg");
  height: 100vh;
  background-size: cover;
  background-position: center;
  display: flex;
  align-items: center;
  justify-content: center;
  flex-direction: column; }

.text-center {
  text-align: center; }

.hero-title {
  max-width: 50%;
  color: #fff; }

.first-name {
  font-family: "Philosopher", sans-serif;
  font-size: 50px; }

.last-name {
  font-family: "Vollkorn", serif;
  font-size: 75px; }

h1 {
  text-align: center; }

.personal-statement {
  width: 50%;
  color: #3fb0ac;
  text-align: center;
  font-size: 20px;
  line-height: 75%;
  font-family: "Philosopher", sans-serif; }

.portfolio {
  height: 400px;
  overflow: hidden;
  padding-top: 20px;
  padding-bottom: 20px; }

.about-me {
  background-color: #dddfd4;
  width: 100%; }

.headshot {
  height: 300px;
  padding-top: 50px;
  opacity: .95; }

.content-title {
  font-family: "Vollkorn", serif;
  font-size: 30px;
  color: #173e43;
  padding-top: 30px;
  padding-bottom: 30px;
  border-bottom: #fae596; }

